(42, 'woocommerce-WCSubscriptions-June-2023-updated-needed', 'marketing', 'en_US', 'Action required: Security update of WooCommerce Subscriptions', '<strong>Your store requires a security update for the WooCommerce Subscriptions plugin</strong>. Please update the WooCommerce Subscriptions plugin immediately to address a potential vulnerability.', '{}', 'pending', 'woocommerce.com', '2025-02-17 14:29:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(43, 'woocommerce-WCReturnsWarranty-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce Returns and Warranty Requests extension', '<strong>Your store requires a security update for the Returns and Warranty Requests extension</strong>.  Please update to the latest version of the WooCommerce Returns and Warranty Requests extension immediately to address a potential vulnerability discovered on May 31.', '{}', 'pending', 'woocommerce.com', '2025-02-17 14:29:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(44, 'woocommerce-WCOPC-June-2023-updated-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce One Page Checkout', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce One Page Checkout extension</strong>. The fix for this vulnerability was released for this extension on June 13th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-02-17 14:29:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(45, 'woocommerce-WCGC-July-2023-update-needed', 'update', 'en_US', 'Action required: Security update of WooCommerce GoCardless Extension', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce GoCardless extension</strong>. The fix for this vulnerability was released on July 4th. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-02-17 14:29:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(46, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage — July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', '{}', 'pending', 'woocommerce.com', '2025-02-17 14:29:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(47, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', '{}', 'pending', 'woocommerce.com', '2025-02-17 14:29:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(48, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-02-17 14:29:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2025-02-17 14:29:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2025-02-17 14:29:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2025-02-17 14:29:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2025-02-17 14:29:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2025-02-17 14:29:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2025-02-17 14:29:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2025-02-17 14:29:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'sirt-woo-2024-11-xss-admin', 'update', 'en_US', 'An important update is required for WooCommerce', 'This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>', '{}', 'pending', 'woocommerce.com', '2025-02-17 14:29:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'woocommerce-analytics-order-attribution-promotions-december-2024', 'marketing', 'en_US', 'Discover what drives your sales', 'Use our powerful <strong>Order Attribution</strong> extension to understand what truly drives your revenue. Track your sales journey, identify your most effective marketing channels, and optimize your sales strategy.', '{}', 'pending', 'woocommerce.com', '2025-02-17 14:29:24', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2025-02-18 14:35:58', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2025-02-19 18:31:09', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'stripe_stripetax_q1_2025', 'marketing', 'en_US', 'Say goodbye to your tax woes', 'Stripe Tax automatically applies the correct sales tax, VAT, or GST, saving time while keeping you compliant. And with simple automatic filing, you can stop stressing at tax time and focus on growing instead.', '{}', 'pending', 'woocommerce.com', '2025-02-19 18:31:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', 'Start accepting payments on your store!', 'Take payments with the provider that’s right for you - choose from 100+ payment gateways for WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2025-02-22 15:35:39', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'printful_promo_q1_2025', 'marketing', 'en_US', 'New: sell print-on-demand products, risk-free', 'Expand your product line with Printful, now available in the WooCommerce Marketplace. Sell custom products with no upfront costs or inventory. This trusted supplier prints, packages, and ships for you, so you can focus on growing your store — and your next big idea.', '{}', 'pending', 'woocommerce.com', '2025-02-26 14:31:57', NULL, 0, 'plain', '', 0, 0, 'info'),
(64, 'paypal-braintree-sdk-depreciation-2025-02-20', 'update', 'en_US', 'Update required for your PayPal Braintree extension', 'To continue securely accepting payments, you must update your PayPal Braintree extension to version 3.2.4 or newer before June 30, 2025. Your current extension version will not support payment processing after this date due to an SSL certificate update.', '{}', 'pending', 'woocommerce.com', '2025-03-04 15:18:20', NULL, 0, 'plain', '', 0, 0, 'info'),
(65, 'wc-admin-real-time-order-alerts', 'info', 'en_US', 'Get real-time order alerts anywhere', 'Get notifications about store activity, including new orders and product reviews directly on your mobile devices with the Woo app.', '{}', 'unactioned', 'woocommerce-admin', '2025-08-13 12:22:16', NULL, 0, 'plain', '', 0, 0, 'info'),
(66, 'uk-vat-notice-2025-04-30', 'warning', 'en_US', 'UK VAT numbers are no longer supported', 'Due to recent HM Revenue &amp; Customs (HMRC) VAT API changes, your EU VAT Number for WooCommerce extension can no longer validate UK VAT numbers. HMRC now requires merchants to register an API key, which this extension does not currently support. We recommend EU/UK VAT Validation Manager for WooCommerce as a replacement', '{}', 'pending', 'woocommerce.com', '2025-08-13 12:22:17', NULL, 0, 'plain', '', 0, 0, 'info'),
(67, 'payson-retirement-2025', 'update', 'en_US', 'Payson Form Retirement', 'Svea Bank is discontinuing the Payson service; the last date for transactions is August 31, 2025. As a result, the Payson Form extension has been removed from WooCommerce.com. Documentation remains available.', '{}', 'pending', 'woocommerce.com', '2025-08-13 12:22:17', NULL, 0, 'plain', '', 0, 0, 'info'),
(68, 'paypal-braintree-sdk-depreciation-2025-05-30', 'update', 'en_US', 'Action required: Update the PayPal Braintree extension', 'Your PayPal Braintree extension is using an outdated SDK and will stop processing payments after June 30, 2025, due to a root SSL certificate expiration. To avoid service disruption, update to version 3.2.4 or newer immediately.', '{}', 'pending', 'woocommerce.com', '2025-08-13 12:22:17', NULL, 0, 'plain', '', 0, 0, 'info'),
(69, 'woohoo10-coupon', 'marketing', 'en_US', 'Celebrate and save with WooCommerce 10.0', 'To mark this milestone release, get 10% off extensions and themes in the official Woo Marketplace. Use code WOOHOO10 at checkout to save on essential tools that help grow your business. Offer ends August 15, 2025. Terms and conditions apply.', '{}', 'unactioned', 'woocommerce.com', '2025-08-13 12:22:17', NULL, 0, 'plain', '', 0, 0, 'info'),
(70, 'stripe_optimizedcheckout_upgrade_q3_2025', 'marketing', 'en_US', 'Upgrade for a faster, more flexible Stripe checkout', 'You\'re not on the latest WooCommerce Stripe Payment Gateway version. Upgrade to version 9.8 to access Stripe\'s Optimized Checkout Suite and leverage AI models to dynamically surface the most relevant payment methods for each customer, including BNPL, local options, and one-click Link checkout.', '{}', 'pending', 'woocommerce.com', '2025-08-13 12:22:17', NULL, 0, 'plain', '', 0, 0, 'info'),
(71, 'stripe_optimizedcheckout_enable_q3_2025', 'marketing', 'en_US', 'Unlock dynamic payments and sell more', 'You’re on the latest WooCommerce Stripe Payment Gateway — now unlock its full potential by enabling the Optimized Checkout Suite. It uses AI models to dynamically surface the most relevant payment methods for each customer, helping you boost conversions.', '{}', 'pending', 'woocommerce.com', '2025-08-13 12:22:17', NULL, 0, 'plain', '', 0, 0, 'info'),
(72, 'stripe_optimizedcheckout_promotion_q3_2025', 'marketing', 'en_US', 'Upgrade to Stripe for a faster, more flexible checkout', 'Unlock dynamic payments with Stripe’s Optimized Checkout Suite — now available with the WooCommerce Stripe Payment Gateway. Leverage AI models to dynamically surface the most relevant payment methods for each customer, including BNPL, local options, and one-click Link checkout.', '{}', 'pending', 'woocommerce.com', '2025-08-15 17:09:28', NULL, 0, 'plain', '', 0, 0, 'info'),
(73, 'wc-admin-manage-orders-on-the-go', 'info', 'en_US', 'Manage your orders on the go', 'Look for orders, customer info, and process refunds in one click with the Woo app.', '{}', 'unactioned', 'woocommerce-admin', '2025-08-16 15:01:33', NULL, 0, 'plain', '', 0, 0, 'info'),
(74, 'wc-admin-email-improvements', 'info', 'en_US', 'Store emails have had an upgrade!', 'We’ve made some exciting improvements to our email templates, including modern, shopper-friendly designs and new customization options. And if you’re using a block theme, you can automatically sync your theme styles! Head to your email settings to explore the new features.', '{}', 'unactioned', 'woocommerce-admin', '2025-08-19 15:14:04', NULL, 0, 'plain', '', 0, 0, 'info'),
(75, 'paypaladvantage_q3_2025', 'marketing', 'en_US', 'Add PayPal Checkout and earn up to $1,000*', 'PayPal Checkout can boost conversion by up to 46% and average order value by up to 62% with no disruption to your checkout. New US merchants who activate by August 31 save 25% on transaction fees for 6 months. Excludes Venmo. Terms apply.*', '{}', 'pending', 'woocommerce.com', '2025-08-20 14:45:32', NULL, 0, 'plain', '', 0, 0, 'info'),
(76, 'woo-subscription-gifting-bundling-2025-08-18', 'marketing', 'en_US', 'Grow recurring revenue with gifting', 'WooCommerce Subscriptions now includes gifting. Customers can buy subscriptions for others, helping you reach new shoppers and grow recurring revenue with less effort. Update to the latest version of Subscriptions (7.8.0) to start offering gifting.', '{}', 'pending', 'woocommerce.com', '2025-08-20 14:45:32', NULL, 0, 'plain', '', 0, 0, 'info'),
(77, 'stripe_optimizedcheckout_competitors_q3_2025', 'marketing', 'en_US', 'Upgrade to the official WooCommerce Stripe Payment Gateway', 'Unlock dynamic payments with Stripe’s Optimized Checkout Suite — now available with the WooCommerce Stripe Payment Gateway. Use AI models to dynamically surface the most relevant payment methods for each customer, including BNPL, local options, and one-click checkout with Link.', '{}', 'pending', 'woocommerce.com', '2025-08-28 21:26:57', NULL, 0, 'plain', '', 0, 0, 'info');
